actions.script:
import java.math.BigDecimal import java.math.RoundingMode
def isAllMerchant = !merchantId || "__ALL__".equalsIgnoreCase(merchantId) || "ALL".equalsIgnoreCase(merchantId) displayMerchantName = isAllMerchant ? "全部商户" : merchantId
candidateListings = [] matchResults = [] matchError = null
def listingFind = ec.entity.find("marketplace.listing.Listing") .orderBy("-createdDate") .limit(30) if (!isAllMerchant) listingFind.condition("publisherId", merchantId) listingFind.disableAuthz() listingFind.list().each { listing -> candidateListings.add([ listingId : listing.listingId, title : listing.title ?: listing.listingId, listingType : listing.listingType ?: "SUPPLY", status : listing.status ?: "ACTIVE", createdDate : listing.createdDate ]) }
selectedListingId = listingId if (!selectedListingId && candidateListings) selectedListingId = candidateListings[0].listingId
selectedMinScore = (minScore ?: "0.6").toString() selectedMaxResults = (maxResults ?: "20").toString()
Closure<Map> getListingInfo = { String id -> if (!id) return [title: id] def infoFind = ec.entity.find("marketplace.listing.Listing") .condition("listingId", id) infoFind.disableAuthz() def value = infoFind.one() return [ title : value?.title ?: id, listingType: value?.listingType ?: "", publisherId: value?.publisherId ] }
if (selectedListingId) { try { Map resp = ec.service.sync().name("marketplace.MarketplaceServices.find#Matches") .parameters([ listingId : selectedListingId, minScore : new BigDecimal(selectedMinScore), maxResults: Integer.valueOf(selectedMaxResults) ]).call() List<Map<String,Object>> serviceMatches = resp.matches ?: [] serviceMatches.each { item -> Map supplyInfo = getListingInfo(item.supplyListingId) Map demandInfo = getListingInfo(item.demandListingId) BigDecimal score = item.matchScore ?: BigDecimal.ZERO matchResults.add([ matchId : item.matchId ?: "", supplyListingId : item.supplyListingId, supplyTitle : supplyInfo.title, demandListingId : item.demandListingId, demandTitle : demandInfo.title, matchScorePercent : score.multiply(new BigDecimal("100")).setScale(1, RoundingMode.HALF_UP), status : item.status ?: "SUGGESTED", suggestedDate : item.suggestedDate ?: item.createdDate ]) } } catch (Exception e) { matchError = e.message ec.logger.warn("匹配服务失败: ${e.message}") } }
